mapper.select:
SELECT a.id,a.username,a.password,a.safe,a.status,a.role,
( SELECT sum(root) FROM role WHERE JSON_CONTAINS ( a.role, CONVERT ( id, CHAR ) ) ) AS root
FROM admin a

    AND a.id = #{id}
    AND username = #{username}

limit 1

SELECT a.id,a.username,a.status,a.login_time,a.login_ip, ( SELECT count(*) FROM login_log WHERE a.id = admin_id) AS login_count, ( SELECT sum(root) FROM role WHERE JSON_CONTAINS ( a.role, CONVERT ( id, CHAR ) ) ) AS root, ( SELECT GROUP_CONCAT( NAME SEPARATOR " | " ) FROM role WHERE JSON_CONTAINS ( a.role, CONVERT ( id, CHAR ) ) ) AS roles FROM admin a
AND a.username LIKE "%${params.username}%" AND a.status = #{params.status}
AND JSON_CONTAINS ( a.role, CONVERT ( #{params.role}, CHAR ) )
