mapper.select:
SELECT * FROM role

    AND status = #{status}
    AND root = #{root}

        AND id IN

            #{item}

SELECT r.id,r.name,r.permission,r.root,r.status,r.create_time,
( SELECT COUNT( * ) FROM admin WHERE JSON_CONTAINS( role, CONVERT(r.id, CHAR))) as admin_count
FROM role r

    AND r.name LIKE "%${params.name}%"
    AND r.status = #{params.status}
    AND r.root = #{params.root}
